mapper.select:
select *from menu where MNAME=#{CMENU} 

	and MSIZE=#{CSIZE}


	 and EDGE=#{CEDGE}

mapper.update:
update orders set ODONEFLAG=#{doneFlag},ODONEDATE=#{doneDate} where  OMCHTTRDNO=#{OMCHTTRDNO}  and OEMAIL=#{OEMAIL} and ONAME=#{ONAME} 

	and OSIZE=#{OSIZE} 


	and OEDGE=#{OEDGE}

update menu set MCOUNT=#{count} where MNAME=#{CMENU}

	and MSIZE=#{CSIZE}


	 and EDGE=#{CEDGE}
